function ExpenseDate(){
    const month = props.date.toLocaleDateString('en-US' , {month :'long'})
    const year = props.date.getFullYear();
    const day = props.date.toLocaleDateString('en-US' , {day:'2-digit'})
    return(
        <div>
        <div>{month}</div>
        <div>{year}</div>
        <div>{day}</div>
      </div>
    )

}
export default ExpenseDate;
